import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { detailsAccessory } from "../actions/accessoryAction";
// import dataAccessory from "../data-accessory";
import EmptyStripe from "./EmptyStripe";
import LoadingBox from "./LoadingBox";
import MessageBox from "./MessageBox";
import Rating from "./Rating";

export default function AccessoryScreen(props) {
  // const productAccessory = dataAccessory.productAccessories.find((x) => x._id === props.match.params.id);
  // if (!productAccessory) {
  //   return <div>Product Not Found</div>;
  // }

  const dispatch = useDispatch();
  const accessoryId = props.match.params.id;
  const [qty, setQty] = useState(1);
  const accessoryDetails = useSelector((state) => state.accessoryDetails);
  const { loading, error, productAccessory } = accessoryDetails;

  useEffect(() => {
    dispatch(detailsAccessory(accessoryId));
  }, [dispatch, accessoryId]);

  const addToCartHandler = () => {
    props.history.push(`/cart/${accessoryId}?qty=${qty}`);
  };
  return (
    <div>
      <EmptyStripe />

      <div className="category">
        <Link to="/dress" className="primary">
          back to
        </Link>
        <div className="max-width">
          {loading ? (
            <LoadingBox></LoadingBox>
          ) : error ? (
            <MessageBox variant="error">{error}</MessageBox>
          ) : (
            <div className="product-content">
              <div className="card-item">
                <img
                  src={productAccessory.image}
                  alt={productAccessory.name}
                ></img>
              </div>
              <div className="card-item">
                <ul className="details-product">
                  <li className="prod-name">{productAccessory.name}</li>
                  <li>
                    <Rating
                      rating={productAccessory.rating}
                      numReviews={productAccessory.numReviews}
                    ></Rating>
                  </li>
                  <li className="prod-price">
                    {" "}
                    Price: ₪ {productAccessory.price}
                  </li>
                  <li className="sale-price">Sale Price : ₪ 3800</li>

                  <li className="description">Description</li>
                  <li className="description-text">
                    {productAccessory.description}
                  </li>

                  <li className="final-price">
                    <div className="title-price">
                      {" "}
                      Final Price: ₪ {productAccessory.price}
                    </div>
                    <div className="status">
                      Status:
                      {productAccessory.countInStock > 0 ? (
                        <span className="success"> In Stock</span>
                      ) : (
                        <span className="error"> Unavailable</span>
                      )}
                    </div>
                    {productAccessory.countInStock > 0 && (
                      <>
                        <li>
                          <div>
                            <span>Quantity: </span>
                            <select
                              value={qty}
                              onChange={(e) => setQty(e.target.value)}
                            >
                              {[
                                ...Array(productAccessory.countInStock).keys(),
                              ].map((x) => (
                                <option key={x + 1} value={x + 1}>
                                  {x + 1}
                                </option>
                              ))}
                            </select>
                          </div>
                        </li>
                        <li>
                          <button
                            onClick={addToCartHandler}
                            className="primary block"
                          >
                            Add to Cart
                          </button>
                        </li>
                      </>
                    )}
                  </li>
                </ul>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
