import axios from "axios";
import {
  SHOE_DETAILS_FAIL,
  SHOE_DETAILS_REQUEST,
  SHOE_DETAILS_SUCCESS,
  SHOE_LIST_FAIL,
  SHOE_LIST_REQUEST,
  SHOE_LIST_SUCCESS,
} from "../constants/shoeConstant";

export const listShoe = () => async (dispatch) => {
  dispatch({
    type: SHOE_LIST_REQUEST,
  });
  try {
    const { data } = await axios.get("/api/productShoes");
    dispatch({ type: SHOE_LIST_SUCCESS, payload: data });
  } catch (error) {
    dispatch({ type: SHOE_LIST_FAIL, payload: error.message });
  }
};

export const detailsShoe = (shoeId) => async (dispatch) => {
  dispatch({ type: SHOE_DETAILS_REQUEST, payload: shoeId });
  try {
    const { data } = await axios.get(`/api/productShoes/${shoeId}`);
    dispatch({ type: SHOE_DETAILS_SUCCESS, payload: data });
  } catch (error) {
    dispatch({
      type: SHOE_DETAILS_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};
