import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { detailsDress } from "../actions/dressAction";
// import dataDress from "../data-dress";
import EmptyStripe from "./EmptyStripe";
import LoadingBox from "./LoadingBox";
import MessageBox from "./MessageBox";
import Rating from "./Rating";

export default function DressScreen(props) {
  // const productDress = dataDress.productDresses.find((x) => x._id === props.match.params.id);
  // if (!productDress) {
  //   return <div>Product Not Found</div>;
  // }
  const dispatch = useDispatch();
  const dressId = props.match.params.id;
  const [qty, setQty] = useState(1);
  const dressDetails = useSelector((state) => state.dressDetails);
  const { loading, error, productDress } = dressDetails;

  useEffect(() => {
    dispatch(detailsDress(dressId));
  }, [dispatch, dressId]);

  const addToCartHandler = () => {
    props.history.push(`/cart/${dressId}?qty=${qty}`);
  };
  return (
    <div>
      <EmptyStripe />

      <div className="category">
        <Link to="/dress" className="primary">
          back to
        </Link>
        <div className="max-width">
          {loading ? (
            <LoadingBox></LoadingBox>
          ) : error ? (
            <MessageBox variant="error">{error}</MessageBox>
          ) : (
            <div class="product-content">
              <div className="card-item">
                <img src={productDress.image} alt={productDress.name}></img>
              </div>
              <div className="card-item">
                <ul className="details-product">
                  <li className="prod-name">{productDress.name}</li>
                  <li>
                    <Rating
                      rating={productDress.rating}
                      numReviews={productDress.numReviews}
                    ></Rating>
                  </li>
                  <li className="prod-price"> Price: ₪ {productDress.price}</li>
                  <li className="sale-price">Sale Price : ₪ 3800</li>

                  <li className="description">Description</li>
                  <li className="description-text">
                    {productDress.description}
                  </li>

                  <li className="final-price">
                    <div className="title-price">
                      {" "}
                      Final Price: ₪ {productDress.price}
                    </div>
                    <div className="status">
                      Status:
                      {productDress.countInStock > 0 ? (
                        <span className="success"> In Stock</span>
                      ) : (
                        <span className="error"> Unavailable</span>
                      )}
                    </div>
                    {productDress.countInStock > 0 && (
                      <>
                        <li>
                          <div>
                            <span>Quantity: </span>
                            <select
                              value={qty}
                              onChange={(e) => setQty(e.target.value)}
                            >
                              {[
                                ...Array(productDress.countInStock).keys(),
                              ].map((x) => (
                                <option key={x + 1} value={x + 1}>
                                  {x + 1}
                                </option>
                              ))}
                            </select>
                          </div>
                        </li>
                        <li>
                          <button
                            onClick={addToCartHandler}
                            className="primary block"
                          >
                            Add to Cart
                          </button>
                        </li>
                      </>
                    )}
                  </li>
                </ul>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
