import axios from "axios";
import {
  ACCESSORY_DETAILS_FAIL,
  ACCESSORY_DETAILS_REQUEST,
  ACCESSORY_DETAILS_SUCCESS,
  ACCESSORY_LIST_FAIL,
  ACCESSORY_LIST_REQUEST,
  ACCESSORY_LIST_SUCCESS,
} from "../constants/accessoryConstant";

export const listAccessory = () => async (dispatch) => {
  dispatch({
    type: ACCESSORY_LIST_REQUEST,
  });
  try {
    const { data } = await axios.get("/api/productAccessories");
    dispatch({ type: ACCESSORY_LIST_SUCCESS, payload: data });
  } catch (error) {
    dispatch({ type: ACCESSORY_LIST_FAIL, payload: error.message });
  }
};


export const detailsAccessory = (accessoryId) => async (dispatch) => {
  dispatch({ type: ACCESSORY_DETAILS_REQUEST, payload: accessoryId });
  try {
    const { data } = await axios.get(`/api/productAccessories/${accessoryId}`);
    dispatch({ type: ACCESSORY_DETAILS_SUCCESS, payload: data });
  } catch (error) {
    dispatch({
      type: ACCESSORY_DETAILS_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};
