import { applyMiddleware, combineReducers, compose, createStore } from "redux";
import thunk from "redux-thunk";
import { cartReducer } from "./reducers/cartReducer";
import { dressDetailsReducer, dressListReducer } from "./reducers/dressReducer";
import {
  productDetailsReducer,
  productListReducer,
} from "./reducers/productReducer";
import { shoeListReducer } from "./reducers/shoeReducer";

const initialState = {
  cart: {
    cartItems: localStorage.getItem("cartItems")
      ? JSON.parse(localStorage.getItem("cartItems"))
      : [],
  },
};

const reducer = combineReducers({
  productList: productListReducer,
  productDetails: productDetailsReducer,
  dressList: dressListReducer,
  dressDetails: dressDetailsReducer,
  shoeList: shoeListReducer,
  shoeDetails: shoeDetailsReducer,
  cart: cartReducer,
});
const composeEnhancer = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;
const store = createStore(
  reducer,
  initialState,
  composeEnhancer(applyMiddleware(thunk))
);

export default store;
