import {
  DRESS_DETAILS_FAIL,
  DRESS_DETAILS_REQUEST,
  DRESS_DETAILS_SUCCESS,
  DRESS_LIST_FAIL,
  DRESS_LIST_REQUEST,
  DRESS_LIST_SUCCESS,
} from "../constants/dressConstant";

export const dressListReducer = (
  state = { loading: true, productDresses: [] },
  action
) => {
  switch (action.type) {
    case DRESS_LIST_REQUEST:
      return { loading: true };
    case DRESS_LIST_SUCCESS:
      return { loading: false, productDresses: action.payload };
    case DRESS_LIST_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

export const dressDetailsReducer = (
  state = { productDress: {}, loading: true },
  action
) => {
  switch (action.type) {
    case DRESS_DETAILS_REQUEST:
      return { loading: true };
    case DRESS_DETAILS_SUCCESS:
      return { loading: false, productDress: action.payload };
    case DRESS_DETAILS_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};
