import axios from "axios";
import {
  DRESS_DETAILS_FAIL,
  DRESS_DETAILS_REQUEST,
  DRESS_DETAILS_SUCCESS,
  DRESS_LIST_FAIL,
  DRESS_LIST_REQUEST,
  DRESS_LIST_SUCCESS,
} from "../constants/dressConstant";

export const listDress = () => async (dispatch) => {
  dispatch({
    type: DRESS_LIST_REQUEST,
  });
  try {
    const { data } = await axios.get("/api/productDresses");
    dispatch({ type: DRESS_LIST_SUCCESS, payload: data });
  } catch (error) {
    dispatch({ type: DRESS_LIST_FAIL, payload: error.message });
  }
};

export const detailsDress = (dressId) => async (dispatch) => {
  dispatch({ type: DRESS_DETAILS_REQUEST, payload: dressId });
  try {
    const { data } = await axios.get(`/api/productDresses/${dressId}`);
    dispatch({ type: DRESS_DETAILS_SUCCESS, payload: data });
  } catch (error) {
    dispatch({
      type: DRESS_DETAILS_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};
