import {
  ACCESSORY_DETAILS_FAIL,
  ACCESSORY_DETAILS_REQUEST,
    ACCESSORY_DETAILS_SUCCESS,
    ACCESSORY_LIST_FAIL,
    ACCESSORY_LIST_REQUEST,
    ACCESSORY_LIST_SUCCESS,
  } from "../constants/accessoryConstant";

  export const accessoryListReducer = (
    state = { loading: true, productAccessories: [] },
    action
  ) => {
    switch (action.type) {
      case ACCESSORY_LIST_REQUEST:
        return { loading: true };
      case ACCESSORY_LIST_SUCCESS:
        return { loading: false, productAccessories: action.payload };
      case ACCESSORY_LIST_FAIL:
        return { loading: false, error: action.payload };
      default:
        return state;
    }
  };


  export const accessoryDetailsReducer = (
    state = { productAccessory: {}, loading: true },
    action
  ) => {
    switch (action.type) {
      case ACCESSORY_DETAILS_REQUEST:
        return { loading: true };
      case ACCESSORY_DETAILS_SUCCESS:
        return { loading: false, productAccessory: action.payload };
      case ACCESSORY_DETAILS_FAIL:
        return { loading: false, error: action.payload };
      default:
        return state;
    }
  };
