import {
  SHOE_LIST_REQUEST,
  SHOE_LIST_SUCCESS,
  SHOE_LIST_FAIL,
  SHOE_DETAILS_REQUEST,
  SHOE_DETAILS_SUCCESS,
  SHOE_DETAILS_FAIL,
} from "../constants/shoeConstant";

export const shoeListReducer = (
  state = { loading: true, productShoes: [] },
  action
) => {
  switch (action.type) {
    case SHOE_LIST_REQUEST:
      return { loading: true };
    case SHOE_LIST_SUCCESS:
      return { loading: false, productShoes: action.payload };
    case SHOE_LIST_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

export const shoeDetailsReducer = (
  state = { productShoe: {}, loading: true },
  action
) => {
  switch (action.type) {
    case SHOE_DETAILS_REQUEST:
      return { loading: true };
    case SHOE_DETAILS_SUCCESS:
      return { loading: false, productShoe: action.payload };
    case SHOE_DETAILS_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};
